import React, { Component } from 'react';
import PropTypes from 'prop-types';
import './App.css';
import { clearInterval } from 'timers';



class Typer extends Component {
  constructor(props){
    super(props);
    this.state={
      typingSpeed: this.props.speed,
      sentenceArr: [this.props.heading , ...this.props.messages],
      text1: "",
      text2: "",
    }
  }

componentDidMount(){
let counter=0;
this.typing=setInterval(()=>{
  counter+=1;
  this.setState({
    text1: this.state.sentenceArr[0].slice(0, counter),
    text2: this.state.sentenceArr[1].slice(0, counter)
    })
    if(counter===this.state.sentenceArr.join().length){
      clearInterval(this.typing)
    }

}, this.state.typingSpeed)
console.log(counter)

  }


  render(){
    return (
      <>
          <p>{this.state.text1}</p>
          <p> {this.state.text2}</p>
</>
    );
  }
}




Typer.propTypes = {
  speed: PropTypes.number,
  heading: PropTypes.string,
  messages: PropTypes.arrayOf(PropTypes.string),
  infinite: PropTypes.bool
}

class TyperExample extends Component {
  render(){
    return (
      <Typer heading="Lorem ipsum dolor sit amet, consectetur adipiscing elit."
             messages= {["Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.", 
             "Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.", "That's all, folks!" ]}
             speed={100}
             infinite={false}
       ></Typer>
    )
  }
}

export default TyperExample;
