import React, { Component } from 'react';
import PropTypes from 'prop-types';
import './App.css';


class Typer extends Component {
  constructor(props){
    super(props);
    this.state={
      typingSpeed: this.props.speed,
      sentenceArr: [this.props.heading , ...this.props.messages],
      text1: "",
      text2: "",
    }

