//             this.setState({
//               text2: arr[2].slice(0, subCounter)
//           })
//           console.log(counter)
//           }, this.state.typingSpeed)
//         }
//     }, this.state.typingSpeed)
//   })
// }
  let heading="";
  let messages=[];
  let counter=0;
  for (let i=0; i< this.state.sentenceArr.length; i++){
  i===0 ? heading=this.state.sentenceArr[i] : messages.push(this.state.sentenceArr[i]);
  }

  this.typing = (()=>{
    let subCounter=0;
    this.subTyping=setInterval(()=>{
        subCounter+=1;
           this.setState({
           text1: heading.slice(0, subCounter),
           })
           if(subCounter===heading.length){
             clearInterval(this.subTyping)
             counter=1;
          }    
    }, this.state.typingSpeed)
    console.log(this.state.counter)
    if (counter===1){
      subCounter=0;
      this.msgTyping=setInterval(()=>{
        subCounter++;
          this.setState({
            text2: messages[1].slice(0,subCounter)
          })
      },this.state.typingSpeed)
    }
  })
  // if (counter>this.state.sentenceArr.length-2){
  //     clearInterval(this.interval) 
  // }
}

componentDidMount(){
this.typing();
}

  render(){
    return (
      <>
          <p>{this.state.text1}</p>
          <p> {this.state.text2}</p>
      </>
    );
  }
}





Typer.propTypes = {
  speed: PropTypes.number,
  heading: PropTypes.string,
  messages: PropTypes.arrayOf(PropTypes.string),
  infinite: PropTypes.bool
}

class TyperExample extends Component {
  render(){
    return (
      <Typer heading="Lorem ipsum dolor sit amet, consectetur adipiscing elit."
             messages= {["Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.", 
             "Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.", "That's all, folks!" ]}
             speed={100}
             infinite={false}
       ></Typer>
    )
  }
}

export default TyperExample;
